def count_word_occurrences(message):
    # Step 1: Normalize the input
    message_lower = message.lower()

    # Step 2: Split the message into words
    words = message_lower.split()

    # Step 3: Count word occurrences
    word_counts = {}
    for word in words:
        # Using get() method to initialize count as 0 if the word is not in the dictionary
        word_counts[word] = word_counts.get(word, 0) + 1

    # Step 4: Generate the result string
    result_string = ""
    for word, count in word_counts.items():
        result_string += f"{word}{count}"

    # Step 5: Return the result string
    return result_string

# Example usage:
message = "Hello world hello Python World"
result = count_word_occurrences(message)
print(result)

